import React, { useState } from 'react';
import './App.css';

function Form()
//store data 
{
    const [inputData, setInputData] = useState({
        name: '',
        email: '',
        password: '',
        mobile: '',
        gender: '',
        hobbies: [],
    });

    // Handle form input changes (text, checkboxes, radio buttons)
    function handleChanges(e) {
        const { name, value, type, checked } = e.target;

        if (type === 'checkbox') {
            // Handle checkbox inputs for hobbies
            setInputData((prevData) => {
                let updatedHobbies = [...prevData.hobbies];
                if (checked) {
                    // Add hobby to the array
                    updatedHobbies.push(value);
                } else {
                    // Remove hobby from the array
                    updatedHobbies = updatedHobbies.filter((hobby) => hobby !== value);
                }
                return { ...prevData, hobbies: updatedHobbies };
            });
        } else if (type === 'radio') {
            // Handle radio inputs for gender
            setInputData((prevData) => ({
                ...prevData,
                gender: value,
            }));
        }
        else {
            // Handle text inputs for name, email, password, and mobile
            setInputData((prevData) => ({
                ...prevData,
                [name]: value,
            }));
        }
    }

    // Handle form submission
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(inputData); // Log the form data upon submit
    };

    return (
        //form start

        <form className="container" onSubmit={handleSubmit}>
            <div className="header">
                <h1>Registration Form</h1>
            </div>
            <div>
            <label htmlFor="name">Name:</label>
                <input type="text" placeholder="Enter Your Name" name="name" value={inputData.name} onChange={handleChanges} required />
            </div>


            <div>
            <label htmlFor="email">Email:</label>
                <input type="email" placeholder="Enter Your Email" name="email" value={inputData.email} onChange={handleChanges} required />
            </div>


            <div>
                <input type="password" placeholder="Enter Your Password" name="password" value={inputData.password} onChange={handleChanges} required />
            </div>

            <div>
                <input type="number" placeholder="Enter Your Mobile No" name="mobile" value={inputData.mobile} onChange={handleChanges} required />
            </div>

            {/*Hobbies data*/}

            <div>
                <label htmlFor="hobbies">Hobbies:</label>
                <div>
                    <input type="checkbox" name="hobbies" value="Singing" checked={inputData.hobbies.includes('Singing')} onChange={handleChanges} />Singing
                </div>

                <div>
                    <input type="checkbox" name="hobbies" value="Badminton" checked={inputData.hobbies.includes('Badminton')} onChange={handleChanges} />Badminton
                </div>
                <div>
                    <input type="checkbox" name="hobbies" value="Cooking" checked={inputData.hobbies.includes('Cooking')} onChange={handleChanges} />Cooking
                </div>

                <div>
                    <input type="checkbox" name="hobbies" value="Reading Books" checked={inputData.hobbies.includes('Reading Books')} onChange={handleChanges} />Reading Books
                </div>
            </div>
            <br />
            {/*Gender data*/}
            <div>
                <label htmlFor="gender">Gender:</label>
                <div>
                    <input type="radio" name="gender" value="male" checked={inputData.gender === 'male'} onChange={handleChanges} />Male
                </div>
                <div>
                    <input type="radio" name="gender" value="female" checked={inputData.gender === 'female'} onChange={handleChanges} />Female
                </div>

                <div>
                    <input type="radio" name="gender" value="other" checked={inputData.gender === 'other'} onChange={handleChanges} />Other
                </div>
            </div>
            <div>
                <button type="submit">Submit</button>
            </div>
        </form>
    );
}

export default Form;
