import React, {useState } from 'react'
import "./App.css"

function Form(){
  
    const [inputData,setInputData]=useState({
        
            name: '',
            email:'',
            password:'',
            mobile:'',
            gender:'',
            hobbies:''
    
    })
  
    function handleData(e) {
        const { name, value, type, checked } = e.target;
      
        if (type === "checkbox") {
          // Handle checkbox for hobbies: Update the array of selected hobbies
          setInputData(prevData => {
            const updatedHobbies = checked
              ? [...prevData.hobbies, value]  // Add value if checked
              : prevData.hobbies.filter(hobby => hobby !== value); // Remove value if unchecked
      
            return {
              ...prevData,
              hobbies: updatedHobbies,
            };
          });
        } else if (type === "radio") {
          // Handle radio button for gender: Update the selected gender
          setInputData(prevData => ({
            ...prevData,
            gender: value,
          }));
        } else {
          // Handle other input types like text, email, number, etc.
          setInputData(prevData => ({
            ...prevData,
            [name]: value, // Dynamically update the field based on the name attribute
          }));
        }
      
        // It's better to log inside a useEffect hook to get the updated state
      }
      


    

  


    return(
      
        <from className='container'>
            <div className='header'>
                <h1>Registration Form</h1>
            </div>
            <div>
            <input type='text' placeholder="Enter Your Name" name="name" value={inputData.name} onChange={handleData}></input>
            </div>
            <div>
            <input type='text' placeholder="Enter Your Email" name="email" value={inputData.email} onChange={handleData}></input>
            </div>
            <div>
            <input type='text' placeholder="Enter Your Password" name="password" value={inputData.password} onChange={handleData} ></input>
            </div>
            <div>
            <input type='number' placeholder="Enter Your  Mobile No" name="mobile" value={inputData.mobile} onChange={handleData} ></input>
            </div> 
            <div>
            <label htmlfor="hobbies">Hobbies</label>:
                <input type="checkbox" name="hobbies"  value={inputData.hobbies} onChange={handleData}/>Singing
                <input type="checkbox" name="hobbies"  value={inputData.hobbies} onChange={handleData}/>Badminton
                <input type="checkbox" name="hobbies"  value={inputData.hobbies} onChange={handleData} />Reading Books
            </div>
            <div>
            <label htmlfor="gender">Gender</label>
                <input type = "radio"  name="gender" value={inputData.gender} onChange={handleData}/>male
                <input type="radio" name="gender" value={inputData.gender} onChange={handleData}/> female
                <input type="radio" name="gender" value={inputData.gender} onChange={handleData}/> other
            </div>
            <div>
                <button type='submit'>Submit </button>
            </div>
        </from>
    )
}
export default Form;
